const { createClient } = require('@supabase/supabase-js');

const supabaseUrl = process.env.SUPABASE_URL;
const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseKey) {
  throw new Error('Credenciais do Supabase não configuradas');
}

const supabase = createClient(supabaseUrl, supabaseKey);

module.exports = supabase;

const supabase = require('../supabase');

/**
 * Middleware de autenticação usando Supabase Auth
 *
 * Valida o JWT token do Supabase usando supabase.auth.getUser()
 * Isso é o método recomendado oficial do Supabase, pois:
 * - Valida o token diretamente com o Supabase Auth
 * - Suporta revogação instantânea de tokens
 * - Não precisa de JWT_SECRET ou JWKS
 * - Sempre sincronizado com o estado real da autenticação
 */
const supabaseMiddleware = async (req, res, next) => {
  try {
    const authHeader = req.headers.authorization;

    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return res.status(401).json({ error: 'Token de acesso requerido' });
    }

    const token = authHeader.substring(7);

    // Validar token usando Supabase Auth API
    // Isso verifica: assinatura, expiração, revogação, etc.
    const { data: { user }, error } = await supabase.auth.getUser(token);

    if (error || !user) {
      console.error('Erro ao validar token Supabase:', error?.message || 'User não encontrado');
      return res.status(401).json({ error: 'Token inválido ou expirado' });
    }

    // Obter role e team_id do nosso banco (public.users)
    let role = 'consultor';
    let teamId = null;
    try {
      const { data: userRow, error: roleError } = await supabase
        .from('users')
        .select('role, team_id')
        .eq('id', user.id)
        .maybeSingle();

      if (roleError) {
        console.error('Erro ao buscar role do usuário:', roleError.message);
        role = 'consultor';
        teamId = null;
      } else if (userRow) {
        if (userRow.role) role = userRow.role;
        if (userRow.team_id) teamId = userRow.team_id;
      } else {
        // No row exists, upsert default profile
        const defaultUserData = {
          id: user.id,
          email: user.email?.toLowerCase() || '',
          name: user.user_metadata?.name || user.user_metadata?.full_name || user.email?.split('@')[0] || 'Usuário',
          role: 'consultor',
          status: 'active',
          team_id: user.user_metadata?.team_id || null,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString()
        };

        const { error: upsertError } = await supabase
          .from('users')
          .upsert(defaultUserData);

        if (upsertError) {
          console.error('Failed to create default user profile:', upsertError.message);
        } else {
          console.log(`Default profile created for new user: ${user.id}`);
        }

        role = 'consultor';
        teamId = defaultUserData.team_id || null;
      }
    } catch (err) {
      console.error('Exceção ao buscar/criar role:', err.message);
      // Fallback to default role
      role = 'consultor';
      teamId = null;
    }

    // Anexar usuário autenticado ao request
    req.user = {
      id: user.id,
      email: user.email,
      role,
      team_id: teamId,
      expiresAt: null // Supabase gerencia expiração
    };

    return next();
  } catch (error) {
    console.error('Erro no middleware Supabase:', error?.message || error);
    return res.status(401).json({ error: 'Erro ao validar autenticação' });
  }
};

module.exports = supabaseMiddleware;

// Controller com Supabase puro (sem JWT custom)
const { createClient } = require('@supabase/supabase-js');
const supabaseAdmin = createClient(process.env.SUPABASE_URL, process.env.SUPABASE_SERVICE_ROLE_KEY);

class SupabaseController {
  static async register(req, res) {
    try {
      const { name, email, password } = req.body || {};
      const { data, error } = await supabaseAdmin.auth.signUp({
        email,
        password,
        options: { data: { name, full_name: name } }
      });
      if (error) throw error;

      return res.status(201).json({
        message: 'Usuário registrado com sucesso',
        user: { id: data.user?.id, email: data.user?.email, name },
        token: null
      });
    } catch (error) {
      console.error('Registration error:', error);
      return res.status(500).json({ error: 'Falha ao registrar usuário. Tente novamente.' });
    }
  }

  static async login(req, res) {
    try {
      const { email, password } = req.body || {};
      const { data, error } = await supabaseAdmin.auth.signInWithPassword({ email, password });
      if (error) throw error;
      // Não gerar token custom. Frontend deve usar access_token do Supabase.
      return res.json({
        message: 'Login realizado com sucesso',
        user: { id: data.user?.id, email: data.user?.email, name: data.user?.user_metadata?.name },
        token: null
      });
    } catch (error) {
      console.error('Login error:', error);
      return res.status(401).json({ error: 'Credenciais inválidas. Tente novamente.' });
    }
  }

  static async logout(req, res) {
    try {
      // Frontend deve encerrar sessão via supabase-js.
      return res.json({ message: 'Logout realizado com sucesso' });
    } catch (error) {
      console.error('Logout error:', error);
      return res.status(500).json({ error: 'Falha ao fazer logout. Tente novamente.' });
    }
  }

  static async showProfile(req, res) {
    return res.json({ message: 'Usuário autenticado', user: req.user });
  }

  static async updateProfile(req, res) {
    try {
      const { name, email } = req.body || {};
      const { data, error } = await supabaseAdmin.auth.admin.updateUserById(req.user.id, {
        email,
        data: name ? { name, full_name: name } : undefined
      });
      if (error) throw error;
      return res.json({ message: 'Usuário atualizado com sucesso', user: { id: data.user.id, email: data.user.email, name } });
    } catch (error) {
      console.error('Update profile error:', error);
      return res.status(500).json({ error: 'Falha ao atualizar perfil. Tente novamente.' });
    }
  }

  static async deleteAccount(req, res) {
    try {
      const { error } = await supabaseAdmin.auth.admin.deleteUser(req.user.id);
      if (error) throw error;
      return res.json({ message: 'Conta deletada com sucesso' });
    } catch (error) {
      console.error('Delete account error:', error);
      return res.status(500).json({ error: 'Falha ao deletar conta. Tente novamente.' });
    }
  }

  // Métodos de compatibilidade
  static async showRegister(req, res) {
    return res.json({
      message: 'Endpoint de registro Supabase',
      methods: ['POST'],
      fields: { required: ['email', 'password', 'name'] }
    });
  }

  static async showSessions(req, res) {
    return res.json({
      message: 'Endpoint de sessão Supabase',
      endpoints: {
        login: 'POST /api/auth/sessions',
        logout: 'DELETE /api/auth/sessions'
      }
    });
  }
}

module.exports = SupabaseController;

const express = require('express');
const router = express.Router();

/**
 * Rotas de Autenticação - Documentação
 *
 * NOTA: Autenticação real é feita diretamente no frontend via Supabase client.
 * Estas rotas servem apenas como documentação para desenvolvedores.
 */

// Rota informativa sobre registro
router.get('/registrations', (req, res) => {
  res.json({
    message: 'Autenticação é feita diretamente no frontend via Supabase client',
    documentation: 'https://supabase.com/docs/guides/auth/auth-signup',
    frontend_usage: 'await supabase.auth.signUp({ email, password, options: { data: { name } } })'
  });
});

// Rota informativa sobre sessões (login/logout)
router.get('/sessions', (req, res) => {
  res.json({
    message: 'Login/Logout é feito diretamente no frontend via Supabase client',
    documentation: 'https://supabase.com/docs/guides/auth/auth-signin',
    frontend_usage: {
      login: 'await supabase.auth.signInWithPassword({ email, password })',
      logout: 'await supabase.auth.signOut()'
    }
  });
});

module.exports = router;

import { createBrowserClient } from '@supabase/ssr'
import type { SupabaseClient } from '@supabase/supabase-js'

// Cache da instância (singleton pattern)
let supabaseClient: SupabaseClient | null = null

/**
 * Retorna ou cria o cliente Supabase para browser (padrão singleton)
 * Valida as credenciais do Supabase e lança erro se não estiverem configuradas
 * @returns Instância cacheada do cliente Supabase
 * @throws Error se NEXT_PUBLIC_SUPABASE_URL ou NEXT_PUBLIC_SUPABASE_ANON_KEY não estiverem definidas
 */
export function createClient(): SupabaseClient {
  // Retorna instância cacheada se já existir
  if (supabaseClient) return supabaseClient

  // Valida variáveis de ambiente
  const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL
  const supabaseAnonKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY

  if (!supabaseUrl || !supabaseAnonKey) {
    throw new Error(
      'Credenciais do Supabase não configuradas no frontend (verifique NEXT_PUBLIC_SUPABASE_URL e NEXT_PUBLIC_SUPABASE_ANON_KEY)'
    )
  }

  // Cria e cacheia a instância
  supabaseClient = createBrowserClient(supabaseUrl, supabaseAnonKey)
  return supabaseClient
}

import { createClient } from '@/lib/supabase'

// Usa o cliente centralizado e cacheado
const supabase = createClient()

export default supabase
export type User = { id: string; email: string | null; name?: string | null; role?: string }
export type RegisterData = { name: string; email: string; password: string; role?: string }
export type LoginData = { email: string; password: string }

async function loadUserRole(userId: string) {
  const { data, error } = await supabase.from('users').select('role').eq('id', userId).single();
  if (error) {
    console.warn('Failed to load user role:', error);
    return 'consultor';
  }
  return data?.role || 'consultor';
}

export { loadUserRole }

'use client'

import React, { useState, useEffect, useContext, createContext, ReactNode } from 'react'
import { useRouter } from 'next/navigation'
import supabase, { User, RegisterData, LoginData } from '@/services/auth'
import { apiRequest } from '@/services/api'

interface AuthContextType {
  user: User | null
  isLoading: boolean
  isAuthenticated: boolean
  login: (credentials: LoginData) => Promise<void>
  register: (userData: RegisterData) => Promise<void>
  logout: () => Promise<void>
  updateProfile: (userData: { name?: string; email?: string }) => Promise<void>
  signOut: () => Promise<void>
}

const AuthContext = createContext<AuthContextType | undefined>(undefined)

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<User | null>(null)
  const [isLoading, setIsLoading] = useState(true)
  const router = useRouter()

  useEffect(() => {
    const initAuth = async () => {
      try {
        const { data } = await supabase.auth.getSession()
        const session = data.session
        if (session?.user) {
          const role = await loadUserRole(session.user.id)
          setUser({ 
            id: session.user.id, 
            email: session.user.email || null, 
            name: session.user.user_metadata?.name || session.user.user_metadata?.full_name,
            role
          })
        } else {
          setUser(null)
        }
      } catch (error) {
        console.error('❌ Erro ao inicializar auth:', error)
        setUser(null)
      } finally {
        setIsLoading(false)
      }
    }

    initAuth()
  }, [])

  const login = async (credentials: LoginData) => {
    const { data, error } = await supabase.auth.signInWithPassword(credentials)
    if (error) throw error
    const role = await loadUserRole(data.user!.id)
    setUser({ 
      id: data.user!.id, 
      email: data.user!.email || null, 
      name: data.user!.user_metadata?.name || data.user!.user_metadata?.full_name,
      role 
    })
    router.push('/dashboard')
  }

  const register = async (userData: RegisterData) => {
    const { data, error } = await supabase.auth.signUp({
      email: userData.email,
      password: userData.password,
      options: { data: { name: userData.name, full_name: userData.name } }
    })
    if (error) throw error
    if (data.user) {
      // Set initial user without role
      setUser({ 
        id: data.user.id, 
        email: data.user.email || null, 
        name: userData.name,
        role: 'consultor' // temporary
      })

      // If role selected, set it via API
      if (userData.role) {
        try {
          await apiRequest('/api/users/select-role', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ role: userData.role })
          })
          // Update local user with selected role
          setUser(prev => prev ? { ...prev, role: userData.role } : null)
        } catch (selectError) {
          console.warn('Failed to set selected role, using default', selectError)
          // Keep 'consultor'
        }
      }

      router.push('/dashboard')
    }
  }

  const logout = async () => {
    await supabase.auth.signOut()
    setUser(null)
    router.push('/login')
  }

  const updateProfile = async (userData: { name?: string; email?: string }) => {
    const { data, error } = await supabase.auth.updateUser({
      email: userData.email,
      data: userData.name ? { name: userData.name, full_name: userData.name } : undefined,
    })
    if (error) throw error
    if (data.user) {
      const role = await loadUserRole(data.user.id)
      setUser({ 
        id: data.user.id, 
        email: data.user.email || null, 
        name: data.user.user_metadata?.name || data.user.user_metadata?.full_name,
        role 
      })
    }
  }

  const signOut = async () => {
    // Endpoint admin server-side deverá tratar deleção definitiva
    await supabase.auth.signOut()
    setUser(null)
    router.push('/login')
  }

  const value: AuthContextType = {
    user,
    isLoading,
    isAuthenticated: !!user,
    login,
    register,
    logout,
    updateProfile,
    signOut,
  }

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>
}

async function loadUserRole(userId: string) {
  const { data, error } = await supabase.from('users').select('role').eq('id', userId).single()
  if (error) {
    console.warn('Failed to load user role:', error)
    return 'consultor'
  }
  return data?.role || 'consultor'
}

export function useAuth() {
  const context = useContext(AuthContext)
  if (context === undefined) {
    throw new Error('useAuth deve ser usado dentro de um AuthProvider')
  }
  return context
}

// Hook para proteger páginas que requerem autenticação
export function useAuthGuard() {
  const { isAuthenticated, isLoading } = useAuth()
  const router = useRouter()

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      router.push('/login')
    }
  }, [isAuthenticated, isLoading, router])

  return { isAuthenticated, isLoading }
}

// Hook para redirecionar usuários autenticados
export function useGuestGuard() {
  const { isAuthenticated, isLoading } = useAuth()
  const router = useRouter()

  useEffect(() => {
    if (!isLoading && isAuthenticated) {
      router.push('/dashboard')
    }
  }, [isAuthenticated, isLoading, router])

  return { isAuthenticated, isLoading }
}

'use client'

import { useEffect } from 'react'
import { useRouter } from 'next/navigation'
import { useAuth } from '@/hooks/useAuth'
import { LoadingPage } from '@/components/ui/loading-spinner'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { AlertTriangle, ArrowLeft } from 'lucide-react'

interface ProtectedRouteProps {
  children: React.ReactNode
  requireRole?: 'admin' | 'user' | 'any'
  fallback?: React.ReactNode
}

export function ProtectedRoute({ 
  children, 
  requireRole = 'any',
  fallback 
}: ProtectedRouteProps) {
  const { isAuthenticated, user, isLoading } = useAuth()
  const router = useRouter()

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      const currentPath = window.location.pathname
      router.replace(`/login?redirect=${encodeURIComponent(currentPath)}`)
      return
    }
  }, [isAuthenticated, isLoading, router])

  // Ainda carregando
  if (isLoading) {
    return fallback || <LoadingPage text="Verificando autenticação..." />
  }

  // Não autenticado
  if (!isAuthenticated) {
    return fallback || <LoadingPage text="Redirecionando para login..." />
  }

  // Verificar role se necessário
  if (requireRole !== 'any') {
    const userRole = user?.role || 'user'
    const isEmailAdmin = user?.email?.toLowerCase().includes('admin')
    const hasAdminAccess = userRole === 'admin' || isEmailAdmin

    if (requireRole === 'admin' && !hasAdminAccess) {
      return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 p-4">
          <Card className="w-full max-w-md">
            <CardHeader className="text-center">
              <div className="mx-auto mb-4 flex h-12 w-12 items-center justify-center rounded-full bg-red-100">
                <AlertTriangle className="h-6 w-6 text-red-600" />
              </div>
              <CardTitle className="text-xl">Acesso Negado</CardTitle>
              <CardDescription>
                Você não tem permissão para acessar esta página.
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="text-center text-sm text-gray-600">
                Esta área é restrita a administradores.
              </div>
              <Button 
                onClick={() => router.push('/dashboard')}
                className="w-full"
                variant="outline"
              >
                <ArrowLeft className="mr-2 h-4 w-4" />
                Voltar ao Dashboard
              </Button>
            </CardContent>
          </Card>
        </div>
      )
    }
  }

  return <>{children}</>
}

export default ProtectedRoute
