import React, { useState } from 'react';

interface Props {
  title: string;
  onAction: () => void;
}

export const Example: React.FC<Props> = ({ title, onAction }) => {
  const [count, setCount] = useState(0);

  return (
    <div className="example-container">
      <h1>{title}</h1>
      <p>Count: {count}</p>
      <button onClick={() => setCount(count + 1)}>
        Increment
      </button>
      <button onClick={onAction}>
        Execute Action
      </button>
    </div>
  );
};

.example-container {
  padding: 20px;
  background: #f5f5f5;
  border-radius: 8px;
  max-width: 600px;
  margin: 0 auto;
}

.example-container h1 {
  color: #333;
  font-size: 24px;
  margin-bottom: 16px;
}

.example-container button {
  padding: 10px 20px;
  margin: 5px;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
}

.example-container button:hover {
  background: #0056b3;
}
